INSERT INTO `dokumen_chunk` (`id`, `dokumen_id`, `sop_id`, `no_urut`, `isi_chunk`, `halaman`, `bagian`, `created_at`, `updated_at`) VALUES (201, 1, 40, 2, 'STANDAR PELAYANAN PENGURUSAN BEASISWA BANK INDONESIA - Sistem, Mekanisme, dan Prosedur: Mekanisme beasiswa Bank Indonesia berawal dari calon mahasiswa melakukan pendaftaran online melalui pendaftaran online yang diberikan. Direktorat Prestasi Mahasiswa dan Hubungan Kealumnian melakukan rekapitulasi mahasiswa yang mendaftar sebagai calon penerima beasiswa dari masing-masing jalur, kemudian melakukan seleksi berkas dan verifikasi. Setelah selesai dilakukan seleksi dan verifikasi, Direktorat Prestasi Mahasiswa dan Hubungan Kealumnian mengirimkan berkas ke Bank Indonesia untuk dilakukan wawancara dan seleksi final. Daftar nama mahasiswa yang lulus Beasiswa dikirimkan kembali ke Direktorat Prestasi Mahasiswa dan Hubungan Kealumnian dan Direktorat Prestasi Mahasiswa dan Hubungan Kealumnian mengumumkan nama- nama penerima beasiswa dengan menerbitkan Surat Edaran Pemberi Beasiswa dan mengumumkan melalui website dan Fakultas. Dana beasiswa nantinya akan di transfer ke rekening masing-masing mahasiswa penerima beasiswa oleh donatur.', 70, 'Sistem, Mekanisme, dan Prosedur', '2025-12-10 01:55:26', '2025-12-10 01:55:26');
