INSERT INTO `dokumen_chunk` (`id`, `dokumen_id`, `sop_id`, `no_urut`, `isi_chunk`, `halaman`, `bagian`, `created_at`, `updated_at`) VALUES (210, 1, 41, 1, 'STANDAR PELAYANAN PENGURUSAN BEASISWA VAN DEFENTER - Persyaratan Pelayanan: f. Berasal dari keluarga ekonomi tidak mampu (dibuktikan dengan Surat Keterangan tidak mampu (SKTM) dan atau Surat rekomendasi dari pihak kampus yang menyatakan kondisi keuangan/kemampuan ekonomi mahasiswa yang bersangkutan). g. Indeks Prestasi Kumulatif ≥ 3.00. h. Jurusan: pertanian, perikanan, peternakan, arsitektur, seni dan budaya, biologi, kimia, matematika, fisika, komunikasi, ekonomi, pendidikan, teknik, bahasa, manajemen, kehutanan, geografi, kesehatan/kedokteran, sejarah, informatika, statistik (kecuali hukum, ilmu politik, hubungan internasional, ilmu administrasi negara, agama, dan teologi). i. Aktif berorganisasi atau komonitas. j. Fasih berbahasa Inggris diutamakan.', 72, 'Persyaratan Pelayanan', '2025-12-10 01:55:26', '2025-12-10 01:55:26');
INSERT INTO `dokumen_chunk` (`id`, `dokumen_id`, `sop_id`, `no_urut`, `isi_chunk`, `halaman`, `bagian`, `created_at`, `updated_at`) VALUES (211, 1, 41, 2, 'STANDAR PELAYANAN PENGURUSAN BEASISWA VAN DEFENTER - Sistem, Mekanisme, dan Prosedur: Mekanisme beasiswa Van Defenter Maas berawal dari calon mahasiswa melakukan pendaftaran online melalui pendaftaran online yang diberikan. Biro Kemahasiswaan dan Kealamunian melakukan rekapitulasi mahasiswa yang mendaftar sebagai calon penerima beasiswa dari masing masing jalur, kemudian melakukan seleksi berkas dan verifikasi. Setelah selesai dilakukan seleksi dan verifikasi, Biro Kemahasiswaan dan Kealumnian mengirimkan berkas ke Van Defenter Maas untuk dilakukan wawancara dan seleksi final. Daftar nama mahasiswa yang lulus beasiswa dikirimkan kembali ke Biro Kemahasiswaan dan Kealumnian dan Biro Kemahasiswaan dan Kealumnian mengumumkan nama-nama penerima beasiswa dengan menerbitkan Surat Edaran Pemberi Beasiswa dan mengumumkan melalui website dan Fakultas. Dana beasiswa nantinya akan di transfer ke rekening masing-masing mahasiswa penerima beasiswa oleh donatur.', 72, 'Sistem, Mekanisme, dan Prosedur', '2025-12-10 01:55:26', '2025-12-10 01:55:26');
