INSERT INTO `sop_komponen` (`id`, `sop_id`, `jenis`, `judul`, `isi`, `halaman`, `created_at`, `updated_at`) VALUES (95, 38, 'sistem_prosedur', 'Sistem, Mekanisme, dan Prosedur', 'Mekanisme beasiswa KIP-Kuliah berawal dari calon mahasiswa melakukan pendaftaran online melalui SIM KIP-Kuliah. Calon mahasiswa mengikuti ujian Seleksi Nasional Masuk Perguruan Tinggi Negeri, Seleksi Bersama Masuk Perguruan Tinggi Negeri setelah dinyatakan lulus masuk ke USU maka pada kartu peserta tertera sebagai calon penerima beasiswa Kartu Indoneisa Pintar (KIP) kuliah. Direktorat Prestasi Mahasiswa dan Hubungan Kealumnian melakukan rekapitulasi mahasiswa yang mendaftar sebagai calon penerima beasiswa dari masing masing jalur, kemudian melakukan wawancara, seleksi berkas dan visitasi. Setelah selesai dilakukan seleksi dan verifikasi, Direktorat Prestasi Mahasiswa dan Hubungan Kealumnian mengumumkan nama-nama penerima beasiswa dengan menerbitkan Surat Keputusan Rektor dan mengumumkan melalui website dan Fakultas. Surat Keputusan Rektor dikirimkan ke Kementerian untuk pengajuan dana beasiswa yang nantinya akan di transfer ke rekening masing-masing mahasiswa penerima beasiswa.', 65, '2025-12-10 01:55:25', '2025-12-10 01:55:25');
